class Asignacion extends BaseAsignacion {
    
    /**
     * Obtiene el nombre del nuevo discipulo para asignar
     * @return String Nombre del nuevo discipulo 
     */
    public function __toString() {
        $discipulo = $this->getDiscipuloNuevo();
        return $discipulo->getFirstName() . " " . $discipulo->getLastName();
    }

    /**
     * Asigna un discipulo nuevo a un lider
     * @param type $id_lider Identificador del lider 
     * @param type $id_nuevo Identificador del nuevo discipulo
     * @return String mensaje de confirmaciÃ³n OK o error 
     */
    public function asignarNuevos($id_lider, $id_nuevo) {
        $this->setDiscipuloLiderId($id_lider);
        $this->setFecha(date('Y-m-d'));
        $this->setDiscipuloNuevoId($id_nuevo);
        $this->save();
        return 'ok';
    }

}